          chemistry : 98,
          biology : 70
        }
      },
      {
        annual : {
          english : 88,
          maths : 98,
          physics : 95,
          chemistry : 98,
          biology : 79
        }
      }
       ]
     }
   ],
   Initial:true,
   showMarks:false,
   value:"",
  }
  storeName=(event)=>{
    this.setState({
      value:event.target.value
    })
  }
  searchPerson=()=>{
       console.log("here");

    if(this.state.Initial){
    this.setState(
      {
        Initial:false,
        showMarks:true
      }
    )    
    }
  }

 render()
 {
    const studentProfile=""; 
   if(this.state.showMarks){

    const name=this.state.value;
    console.log(this.state.names.includes(name));
    if(this.state.names.includes(name)){
      let index=this.state.names.indexOf(name)
      this.studentProfile=(
        <div>
          <StudentProfile name={name} school={this.state.studentDetails[index].school} dob={this.state.studentDetails[index].dateOfBirth}/>
        </div>)
    }
    else{
        this.studentProfile=<h2>Name Not Found</h2>
    }
   }
   return(
  <div className={classes.StudentDatabase}>
    <h1>Student Mark DataBase</h1>
    <div className={classes.searchDiv}>
      <input type="text" className={classes.textField} placeholder="Enter the Student Name" onChange={this.storeName}/>
      <input type="button" className={classes.searchButton} value="Search" onClick={this.searchPerson} />
      {studentProfile}
    </div>
  </div>
   )
 }
}

export default SearchStudent;
