import React, { Component } from "react";
import classes from "./GitRepo.module.css";
import Search from "./Search";
class GitRepo extends Component {
  state = {
    userName: "",
    repoName: "",
    repos: [],
    HasRepo: false,
    noRepo:false
  };
  changeUser = event => {
    this.setState({
      userName: event.target.value,
    });
  };
  changeRepo = event => {
    this.setState({
      repoName: event.target.value,
    });
  };
  searchUser = () => {
    const requestUrl =
      "https://api.github.com/users/" + this.state.userName + "/repos";
    fetch(requestUrl)
      .then(res => res.json())
      .then(json => {
        console.log(json);
        if(json===[]){
          this.setState({
            noRepo:true,
            HasRepo:false
          })
          return
        }
        this.setState({
          repos: json,
          HasRepo: true,
          noRepo:false
        });
      });
  };
  render() {
    let DisplayRepo = "";
    if (this.state.HasRepo) {
      DisplayRepo = this.state.repos.map(repo => {
        return <p className={classes.displayRepo} key={repo.name}>{repo.name}</p>;
      });
    }
    if(this.state.noRepo){
      DisplayRepo = (<p>No Git Found</p>)
    }
    return (
      <div className={classes.Container}>
        <Search
          changeUser={this.changeUser}
          changeRepo={this.changeRepo}
          searchUser={this.searchUser}
        />
        {DisplayRepo}
      </div>
    );
  }
}

export default GitRepo;
